import React from "react";
import { FieldArray } from "formik";
import { TextField } from "@mui/material";
import { Button } from "@mui/material";
import { generate } from "shortid";

const EnemyForm = ({ formik }) => {
  return (
    <FieldArray name="enemies">
      <div>
        <div>
          {formik.values.enemies.map((combatant, index) => {
            const name = `enemies[${index}.name]`;
            const hp = `enemies[${index}.hp]`;
            const init = `enemies[${index}.init]`;
            const bonus = `enemies[${index}.bonus]`;
            return (
              <div key={combatant.id}>
                <TextField
                  name={name}
                  value={combatant.name}
                  onChange={formik.handleChange}
                />
                <TextField
                  name={hp}
                  value={combatant.hp}
                  onChange={formik.handleChange}
                />
                <TextField
                  name={init}
                  value={combatant.init}
                  onChange={formik.handleChange}
                />
                <TextField
                  name={bonus}
                  value={combatant.bonus}
                  onChange={formik.handleChange}
                />
              </div>
            );
          })}
        </div>
      </div>
    </FieldArray>
  );
};

<FieldArray
  name="friends"
  render={(arrayHelpers) => (
    <div>
      <div>
        {formik.values.enemies.map((combatant, index) => {
          const name = `enemies[${index}.name]`;
          const hp = `enemies[${index}.hp]`;
          const init = `enemies[${index}.init]`;
          const bonus = `enemies[${index}.bonus]`;
          return (
            <div key={combatant.id}>
              <TextField
                name={name}
                value={combatant.name}
                onChange={formik.handleChange}
              />
              <TextField
                name={hp}
                value={combatant.hp}
                onChange={formik.handleChange}
              />
              <TextField
                name={init}
                value={combatant.init}
                onChange={formik.handleChange}
              />
              <TextField
                name={bonus}
                value={combatant.bonus}
                onChange={formik.handleChange}
              />
            </div>
          );
        })}
      </div>
    </div>
  )}
/>;

export default EnemyForm;
