import { TextField } from "@mui/material";
import { FieldArray, Form, Field } from "formik";
import React from "react";
import { Button } from "@mui/material";
import { generate } from "shortid";

const FormA = ({ formik, handleChange }) => {
  return (
    <FieldArray
      name="enemies"
      render={(arrayHelpers) => (
        <div>
          {formik.values.enemies.map((combatant, index) => {
            return (
              <div key={combatant.id}>
                <Field name={`combatant.${index}.name`} />
                <TextField
                  name={combatant.name}
                  value={combatant.name}
                  onChange={handleChange}
                />
                <TextField name={combatant.hp} value={combatant.hp} />
              </div>
            );
          })}
          <pre>{JSON.stringify(formik.values.enemies, null, 2)}</pre>
        </div>
      )}
    />
  );
};

export default FormA;
