import React from "react";
import { Formik } from "formik";
import PlayerForm from "./PlayersForm";
import EnemyForm from "./EnemyForm";
import ButtonBar from "../ButtonBar";

const CombatantForm = () => {
  // will hold access to formikProps.submitForm, to trigger form submission outside of the form
  const submitMyForm = null;

  const handleSubmitMyForm = (e) => {
    if (this.submitMyForm) {
      this.submitMyForm(e);
    }
  };
  const bindSubmitForm = (submitForm) => {
    this.submitMyForm = submitForm;
  };

  return (
    <div>
      <button onClick={this.handleSubmitMyForm}>Submit from outside</button>
      <PlayerForm bindSubmitForm={this.bindSubmitForm} />
      <ButtonBar></ButtonBar>
    </div>
  );
};

export default CombatantForm;
