import { Container } from "@mui/system";
import { Button } from "@mui/material";
import React, { useState, useContext } from "react";
import { useFormik, Form, Field, FieldArray } from "formik";
import * as Yup from "yup";
import DataContext from "../DataContext";

function CombatantFormFields(props) {
  const { pushData } = useContext(DataContext);
  const [combatants, setCombatants] = useState([
    { name: "", hp: "", init: 0, bonus: 0, type: "player" },
    { name: "", hp: "", init: 0, bonus: 0, type: "player" },
  ]);

  const handleChange = (event, index) => {
    const values = [...combatants];
    values[index][event.target.name] = event.target.value;
    setCombatants(values);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    pushData(combatants);
  };

  const handleNewField = (type) => {
    setCombatants([
      ...combatants,
      {
        name: "",
        hp: "",
        init: 0,
        bonus: 0,
        type: type,
      },
    ]);
  };

  const handleRemoveField = (index) => {
    const values = [...combatants];
    values.splice(index, 1);
    setCombatants(values);
  };

  return (
    <div>
      <div>
        <h1>{props.name}</h1>
        <Button onClick={handleNewField}>Add new</Button>
      </div>
      {combatants.map((combatant, index) => (
        <Container key={index}>
          <input
            type="text"
            name="name"
            placeholder="name"
            value={combatant.name}
            onChange={(event) => handleChange(event, index)}
          ></input>
          <input
            type="text"
            name="hp"
            placeholder="hp"
            value={combatant.hp}
            onChange={(event) => handleChange(event, index)}
          ></input>
          <input
            type="text"
            name="init"
            placeholder="init"
            value={combatant.init}
            onChange={(event) => handleChange(event, index)}
          ></input>
          <input
            type="text"
            name="bonus"
            placeholder="bonus"
            value={combatant.bonus}
            onChange={(event) => handleChange(event, index)}
          ></input>
          <Button onClick={() => handleRemoveField(index)}>X</Button>
        </Container>
      ))}
    </div>
  );
}

export default CombatantFormFields;
