import { TextField } from "@mui/material";
import { FieldArray } from "formik";
import React from "react";

const FormA = ({ formik, handleChange }, props) => {
  return (
    <FieldArray
      name={`formik.values.enemies`}
      render={(arrayHelpers) => (
        <div>
          {formik.values.combatants.map((combatant, index) => {
            const name = `combatants[${index}.name]`;
            const errorMessage = getIn(errors, name);
            return (
              <div key={combatant.id}>
                <TextField
                  name={name}
                  value={combatant.name}
                  onChange={handleChange}
                />
                <TextField
                  name={`combatants[${index}.hp]`}
                  value={combatant.hp}
                  onChange={handleChange}
                />
                <Button onClick={() => remove(index)}>x</Button>
              </div>
            );
          })}
          <pre>{JSON.stringify(formik.values.enemies, null, 2)}</pre>
        </div>
      )}
    />
  );
};

export default FormA;
