import React from "react";
import { FieldArray } from "formik";
import { TextField } from "@mui/material";
import { Button } from "@mui/material";
import { generate } from "shortid";

const EnemyForm = ({ formik }) => {
  return (
    <FieldArray name="enemies">
      {({ push }) => (
        <div>
          <div>
            <Button
              type="button"
              onClick={() =>
                push({
                  id: generate(),
                  name: "",
                  hp: "",
                  init: "",
                  bonus: "",
                  type: "enemy",
                })
              }
            >
              Add New Field
            </Button>
          </div>
          <div>
            {formik.values.enemies.map((combatant, index) => {
              const name = `enemies[${index}.name]`;
              const hp = `enemies[${index}.hp]`;
              const init = `enemies[${index}.init]`;
              const bonus = `enemies[${index}.bonus]`;
              return (
                <div key={combatant.id}>
                  <TextField
                    name={name}
                    value={combatant.name}
                    onChange={formik.handleChange}
                  />
                  <TextField
                    name={hp}
                    value={combatant.hp}
                    onChange={formik.handleChange}
                  />
                  <TextField
                    name={init}
                    value={combatant.init}
                    onChange={formik.handleChange}
                  />
                  <TextField
                    name={bonus}
                    value={combatant.bonus}
                    onChange={formik.handleChange}
                  />
                </div>
              );
            })}
          </div>
        </div>
      )}
    </FieldArray>
  );
};

<FieldArray
  name="friends"
  render={(arrayHelpers) => (
    <div>
      {values.friends && values.friends.length > 0 ? (
        values.friends.map((friend, index) => (
          <div key={index}>
            <Field name={`friends.${index}`} />
            <button
              type="button"
              onClick={() => arrayHelpers.remove(index)} // remove a friend from the list
            >
              -
            </button>
            <button
              type="button"
              onClick={() => arrayHelpers.insert(index, "")} // insert an empty string at a position
            >
              +
            </button>
          </div>
        ))
      ) : (
        <button type="button" onClick={() => arrayHelpers.push("")}>
          {/* show this when user has removed all friends from the list */}
          Add a friend
        </button>
      )}
      <div>
        <button type="submit">Submit</button>
      </div>
    </div>
  )}
/>;

export default EnemyForm;
