  const bindSubmitFormB = (submitForm) => {
    submitMyForm = submitForm;
  };

  return (
    <div>
      <button onClick={handleSubmitMyForm}>Submit from outside</button>
      <PlayerForm bindSubmitForm={bindSubmitFormA} />
      <PlayerForm bindSubmitForm={bindSubmitFormB} />
      <ButtonBar></ButtonBar>
    </div>
  );
};

export default CombatantForm;
