import React from "react";
import * as Yup from "yup";
import { useFormik, Form, FieldArray } from "formik";
import { Button } from "@mui/material";
import { generate } from "shortid";

const FormA = ({ onChange, refId }) => {
  const formik = useFormik({
    initialValues: {
      name: "",
      email: "",
    },
    validationSchema: Yup.object({
      name: Yup.string().required("This field is required"),
      email: Yup.string()
        .email("Invalid email address")
        .required("Email is required"),
    }),
    onSubmit: () => {
      watchForm();
    },
  });

  React.useImperativeHandle(refId, () => ({
    Submit: async () => {
      await formik.submitForm();
    },
  }));

  function watchForm() {
    if (onChange) {
      onChange({
        values: formik.values,
        validated: formik.isSubmitting
          ? Object.keys(formik.errors).length === 0
          : false,
      });
    }
  }

  return (
    <div>
      {({ values, handleChange, errors }) => (
        <Form>
          <FieldArray name="combatants">
            {({ push, remove }) => (
              <div>
                <div>
                  <h2>Players</h2>
                  <Button
                    type="button"
                    onClick={() => push({ id: generate(), name: "", hp: "" })}
                  >
                    Add New Field
                  </Button>
                </div>
                {values.combatants.map((combatant, index) => {
                  const name = `combatants[${index}.name]`;
                  const errorMessage = getIn(errors, name);
                  return (
                    <div key={combatant.id}>
                      <TextField
                        name={name}
                        value={combatant.name}
                        onChange={handleChange}
                      />
                      <TextField
                        name={`combatants[${index}.hp]`}
                        value={combatant.hp}
                        onChange={handleChange}
                      />
                      {errorMessage && (
                        <div style={{ color: "red" }}>{errorMessage}</div>
                      )}
                      <Button onClick={() => remove(index)}>x</Button>
                    </div>
                  );
                })}
              </div>
            )}
          </FieldArray>
          <button type="submit">Submit</button>
          <pre>{JSON.stringify(values, null, 2)}</pre>
        </Form>
      )}
    </div>
    // <form ref={refId}>
    //   <div className={"form"}>
    //     <h3>Form A</h3>
    //     <div>
    //       <label>Name </label>
    //       <input
    //         name="name"
    //         value={formik.values.name}
    //         onBlur={formik.handleBlur}
    //         onChange={formik.handleChange}
    //       />
    //       {formik.errors.name && (
    //         <div style={{ color: "red" }}>
    //           <small>{formik.errors.name}</small>
    //         </div>
    //       )}
    //     </div>
    //     <br />
    //     <div>
    //       <label>Email </label>
    //       <input
    //         name="email"
    //         value={formik.values.email}
    //         onBlur={formik.handleBlur}
    //         onChange={formik.handleChange}
    //       />
    //       {formik.errors.email && (
    //         <div style={{ color: "red" }}>
    //           <small>{formik.errors.email}</small>
    //         </div>
    //       )}
    //     </div>
    //     <pre>{JSON.stringify(formik.values, null, 2)}</pre>
    //   </div>
    // </form>
  );
};

export default FormA;
