import React, { useContext } from "react";
import { Form, Formik, FieldArray } from "formik";
import DataContext from "../../DataContext";
import { generate } from "shortid";
import { Button, TextField } from "@mui/material";

const PlayerForm = ({ bindSubmitForm }, props) => {
  const { pushPlayers } = useContext(DataContext);
  return (
    <Formik
      initialValues={{ combatants: "" }}
      onSubmit={(values, { setSubmitting }) => {
        console.log("PlayerForm");
        pushPlayers(values);
        setSubmitting(false);
      }}
    >
      {(formikProps) => {
        const { values, handleChange, handleBlur, handleSubmit } = formikProps;
        // bind the submission handler remotely
        bindSubmitForm(formikProps.submitForm);
        return (
          <Form>
            <FieldArray name="combatants">
              {({ push, remove }) => (
                <div>
                  <div>
                    <h2>{props.name}</h2>
                    <Button
                      type="button"
                      onClick={() =>
                        push({
                          id: generate(),
                          name: "",
                          hp: "",
                          type: props.name,
                        })
                      }
                    >
                      Add New Field
                    </Button>
                  </div>
                  {values.combatants.map((combatant, index) => {
                    const name = `combatants[${index}.name]`;
                    return (
                      <div key={combatant.id}>
                        <TextField
                          name={name}
                          value={combatant.name}
                          onChange={handleChange}
                        />
                        <TextField
                          name={`combatants[${index}.hp]`}
                          value={combatant.hp}
                          onChange={handleChange}
                        />
                        <Button onClick={() => remove(index)}>x</Button>
                      </div>
                    );
                  })}
                </div>
              )}
            </FieldArray>
            <button type="submit">Submit</button>
            <pre>{JSON.stringify(values, null, 2)}</pre>
          </Form>
        );
      }}
    </Formik>
  );
};

