import { FieldArray } from "formik";
import React from "react";

const PlayerForm = ({ formik, onChange }) => {
  return (
    <div className={"form"}>
      <FieldArray>
        {({ push, remove }) => (
          <div>
            {formik.values.formA.combatants.map((combatant, index) => {
              return <div key={combatant.id}>{combatant.id}</div>;
            })}
          </div>
        )}
      </FieldArray>
      {console.log(formik.values.formA)}
      <h3>Form B</h3>
      <div>
        <label>Phone number </label>
        <input
          name="formB.phone"
          value={formik.values.formB.phone}
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
        />
        {formik.errors.formB && (
          <div style={{ color: "red" }}>
            <small>{formik.errors.formB.phone}</small>
          </div>
        )}
      </div>

      <pre>{JSON.stringify(formik.values.formB, null, 2)}</pre>
    </div>
  );
};

export default PlayerForm;
