import React, { useState } from "react";
import { useFormik, Form, Field, FieldArray } from "formik";
import * as Yup from "yup";
import { Button } from "@mui/material";

const FormA = ({ onChange, refId }) => {
  const [combatants, setCombatants] = useState([
    { name: "", hp: "", init: 0, bonus: 0, type: "player" },
  ]);
  const handleChange = (event, index) => {
    const values = [...combatants];
    values[index][event.target.name] = event.target.value;
    setCombatants(values);
  };
  const formik = useFormik({
    initialValues: {
      name: "",
      hp: "",
      init: "",
      bonus: "",
    },
    validationSchema: Yup.object({
      name: Yup.string().required("This field is required"),
      hp: Yup.string().required("This field is required"),
      init: Yup.string().required("This field is required"),
      bonus: Yup.string().required("This field is required"),
    }),
    onSubmit: () => {
      watchForm();
    },
  });

  React.useImperativeHandle(refId, () => ({
    Submit: async () => {
      await formik.submitForm();
    },
  }));

  function watchForm() {
    if (onChange) {
      onChange({
        values: formik.values,
        validated: formik.isSubmitting
          ? Object.keys(formik.errors).length === 0
          : false,
      });
    }
  }

  return (
    <form ref={refId}>
      <div>
        {combatants.map((combatant, index) => (
          <div key={index}>
            <input
              type="text"
              name="name"
              placeholder="name"
              value={combatant.name}
              onChange={(event) => handleChange(event, index)}
            ></input>
            <input
              type="text"
              name="hp"
              placeholder="hp"
              value={combatant.hp}
              onChange={(event) => handleChange(event, index)}
            ></input>
            <input
              type="text"
              name="init"
              placeholder="init"
              value={combatant.init}
              onChange={(event) => handleChange(event, index)}
            ></input>
            <input
              type="text"
              name="bonus"
              placeholder="bonus"
              value={combatant.bonus}
              onChange={(event) => handleChange(event, index)}
            ></input>
            <Button onClick={() => handleRemoveField(index)}>X</Button>
          </div>
        ))}
      </div>
      <div className={"form"}>
        <h3>Form A</h3>
        <div>
          <label>Name </label>
          <input
            name="name"
            value={formik.values.name}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
          />
          {formik.errors.name && (
            <div style={{ color: "red" }}>
              <small>{formik.errors.name}</small>
            </div>
          )}
        </div>
        <br />
        <div>
          <label>Email </label>
          <input
            name="email"
            value={formik.values.email}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
          />
          {formik.errors.email && (
            <div style={{ color: "red" }}>
              <small>{formik.errors.email}</small>
            </div>
          )}
        </div>
        <pre>{JSON.stringify(formik.values, null, 2)}</pre>
      </div>
    </form>
  );
};

export default FormA;
