import React from "react";
import * as yup from "yup";
import { Field, FieldArray, useFormik, Form, Formik, getIn } from "formik";
import { TextField, Button } from "@mui/material";
import { generate } from "shortid";

const validationSchema = yup.object().shape({
  combatants: yup.array().of(
    yup.object().shape({
      name: yup.string().max(10),
      hp: yup.string().min(2),
    })
  ),
});

const PlayerForm = () => {
  const formik = useFormik({
    initialValues: {
      id: generate(),
      name: "",
      hp: "",
    },
  });
  return (
    <div>
      {({ values, handleChange, errors }) => (
        <Form
          onSubmit={(e) => {
            console.log(values.combatants);
          }}
        >
          <FieldArray name="combatants">
            {({ push, remove }) => (
              <div>
                <div>
                  <h2>Players</h2>
                  <Button
                    type="button"
                    onClick={() => push({ id: generate(), name: "", hp: "" })}
                  >
                    Add New Field
                  </Button>
                </div>
                {values.combatants.map((combatant, index) => {
                  const name = `combatants[${index}.name]`;
                  const errorMessage = getIn(errors, name);
                  return (
                    <div key={combatant.id}>
                      <TextField
                        name={name}
                        value={combatant.name}
                        onChange={handleChange}
                      />
                      <TextField
                        name={`combatants[${index}.hp]`}
                        value={combatant.hp}
                        onChange={handleChange}
                      />
                      {errorMessage && (
                        <div style={{ color: "red" }}>{errorMessage}</div>
                      )}
                      <Button onClick={() => remove(index)}>x</Button>
                    </div>
                  );
                })}
              </div>
            )}
          </FieldArray>
          <button type="submit">Submit</button>
          <pre>{JSON.stringify(values, null, 2)}</pre>
        </Form>
      )}
    </div>
  );
};

export default PlayerForm;
