import React from "react";
import { Form, FieldArray } from "formik";

const EnemyForm = ({ formik }) => {
  return (
    <div className={"form"}>
      {({ values, handleChange, errors }) => (
        <Form>
          <FieldArray name="combatants">
            {({ push, remove }) => (
              <div>
                <div>
                  <h2>{props.name}</h2>
                  <Button
                    type="button"
                    onClick={() =>
                      push({
                        id: generate(),
                        name: "",
                        hp: "",
                        type: props.name,
                      })
                    }
                  >
                    Add New Field
                  </Button>
                </div>
                {values.combatants.map((combatant, index) => {
                  const name = `combatants[${index}.name]`;
                  const errorMessage = getIn(errors, name);
                  return (
                    <div key={combatant.id}>
                      <TextField
                        name={name}
                        value={combatant.name}
                        onChange={handleChange}
                      />
                      <TextField
                        name={`combatants[${index}.hp]`}
                        value={combatant.hp}
                        onChange={handleChange}
                      />
                      {errorMessage && (
                        <div style={{ color: "red" }}>{errorMessage}</div>
                      )}
                      <Button onClick={() => remove(index)}>x</Button>
                    </div>
                  );
                })}
              </div>
            )}
          </FieldArray>
          <button type="submit">Submit</button>
          <pre>{JSON.stringify(values, null, 2)}</pre>
        </Form>
      )}
      <h3>Form A</h3>
      <div>
        <label>Name </label>
        <input
          name="formA.combatants.name"
          value={formik.values.formA.combatants.name}
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
        />
        {formik.errors.formA && (
          <div style={{ color: "red" }}>
            <small>{formik.errors.formA.name}</small>
          </div>
        )}
      </div>
      <br />
      <div>
        <label>Email </label>
        <input
          name="formA.email"
          value={formik.values.formA.email}
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
        />
        {formik.errors.formA && (
          <div style={{ color: "red" }}>
            <small>{formik.errors.formA.email}</small>
          </div>
        )}
      </div>
      <pre>{JSON.stringify(formik.values.formA, null, 2)}</pre>
    </div>
  );
};

export default EnemyForm;
