import React from "react";
import * as yup from "yup";
import { Field, FieldArray, FieldProps, Form, Formik, getIn } from "formik";
import { TextField, Button } from "@mui/material";
import { generate } from "shortid";

const validationSchema = yup.object().shape({
  combatants: yup.array().of(
    yup.object().shape({
      name: yup.string().max(10),
      hp: yup.string().min(2),
    })
  ),
});

const PlayerForm = () => {
  return (
    <div>
      <Formik
        initialValues={{ combatants: [{ id: "5", name: "asd", hp: "5" }] }}
        onSubmit={() => {}}
        validationSchema={validationSchema}
      >
        {({ values, handleChange }) => (
          <Form>
            <FieldArray name="combatants">
              {({ push }) => (
                <div>
                  <Button
                    type="button"
                    onClick={() => push({ id: generate(), name: "", hp: "" })}
                  >
                    add to list
                  </Button>
                </div>
              )}
            </FieldArray>
            {values.combatants.map((combatant, index) => {
              return (
                <div key={combatant.id}>
                  <TextField
                    name={`combatants[${index}.name]`}
                    value={combatant.name}
                    onChange={handleChange}
                  />
                  <button>X</button>
                </div>
              );
            })}

            <pre>{JSON.stringify(values, null, 2)}</pre>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default PlayerForm;
