import React from "react";
import { useFormik } from "formik";
import { generate } from "shortid";
import { number, string } from "yup";

const PlayerForm = () => {
  const formik = useFormik({
    initialValues: {
      id: generate(),
      name: string,
      hp: number,
    },
    onSubmit: (values) => {
      console.log(JSON.stringify(values, null, 2));
    },
  });
};
