      name: yup.string().max(10),
      hp: yup.string().min(2),
    })
  ),
});

const PlayerForm = () => {
  return (
    <div>
      <Formik
        initialValues={{ combatants: [{ name: "", hp: "" }] }}
        onSubmit={() => {}}
      >
        {() => (
          <Form>
            <TextField>
              <FieldArray name="combatatns">
                {({}) => (
                  <div>
                    <Button type="button" onClick={() => push({})}>
                      add to list
                    </Button>
                  </div>
                )}
              </FieldArray>
            </TextField>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default PlayerForm;
