import React from "react";
import * as yup from "yup";
import { FieldArray, Form, Formik, getIn, withFormik } from "formik";
import { TextField, Button } from "@mui/material";
import { generate } from "shortid";

const validationSchema = yup.object().shape({
  combatants: yup.array().of(
    yup.object().shape({
      name: yup.string().max(10),
      hp: yup.string().min(2),
    })
  ),
});

function FormA(props) {
  const { combatants, handleChange, onChange } = props;

  React.useEffect(() => {
    if (combatants) {
      onChange(combatants);
    }
  }, [combatants]);

  return (
    <div>
      <Formik
        initialValues={{ combatants: [{ id: generate(), name: "", hp: "" }] }}
        onSubmit={(values) =>
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
          }, 500)
        }
        validationSchema={validationSchema}
      >
        {({ values, errors, handleSubmit }) => (
          <Form id="my-form">
            <FieldArray name="combatants">
              {({ push, remove }) => (
                <div>
                  <div>
                    <h2>Players</h2>
                    <Button
                      type="button"
                      onClick={() => push({ id: generate(), name: "", hp: "" })}
                    >
                      Add New Field
                    </Button>
                  </div>
                  {values.combatants.map((combatant, index) => {
                    const name = `combatants[${index}.name]`;
                    const errorMessage = getIn(errors, name);
                    return (
                      <div key={combatant.id}>
                        <TextField
                          name={name}
                          value={combatants.name}
                          onChange={handleChange}
                        />
                        <TextField
                          name={`combatants[${index}.hp]`}
                          value={combatants.hp}
                          onChange={handleChange}
                        />
                        {errorMessage && (
                          <div style={{ color: "red" }}>{errorMessage}</div>
                        )}
                        <Button onClick={() => remove(index)}>x</Button>
                      </div>
                    );
                  })}
                </div>
              )}
            </FieldArray>
            <button type="submit">Submit</button>
            <pre>{JSON.stringify(values, null, 2)}</pre>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default withFormik({
  mapPropsToValues: () => {
    return {
      name: "",
      hp: "",
    };
  },
})(FormA);
