import React from "react";
import ReactDOM from "react-dom";
import FormA from "./FormEnemies";
import FormB from "./FormPlayers";

function CombatantsForm() {
  const [formValues, setFormValues] = React.useState({
    formA: {},
    formB: {},
  });

  function handleFormAChange(values, errors) {
    console.log(errors);
    setFormValues({
      ...formValues,
      formA: values,
    });
  }

  function handleFormBChange(values) {
    setFormValues({
      ...formValues,
      formB: values,
    });
  }

  function handleSubmit() {
    alert(JSON.stringify(formValues, null, 2));
  }

  return (
    <div className="App">
      <h1>Multiple Form in one submition</h1>
      <FormA onChange={handleFormAChange} />
      <FormB onChange={handleFormBChange} />

      <pre>{JSON.stringify(formValues, null, 2)}</pre>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default CombatantsForm;
