import React from "react";
import { Formik } from "formik";
import PlayerForm from "./PlayersForm";
import EnemyForm from "./EnemyForm";
import ButtonBar from "../ButtonBar";

const CombatantForm = ({ submitMyForm, submitMyFormB }) => {
  const handleSubmitMyForm = (e) => {
    if (submitMyForm) {
      submitMyForm(e);
    }
  };

  const handleSubmitMyFormB = (e) => {
    if (submitMyFormB) {
      submitMyFormB(e);
    }
  };

  const bindSubmitForm = (submitForm) => {
    submitMyForm = submitForm;
  };

  const bindSubmitFormB = (submitForm) => {
    submitMyFormB = submitForm;
  };

  async function handleBothSubmits() {
    await handleSubmitMyFormB();
  }

  return (
    <div>
      <button onClick={handleSubmitMyFormB}>Submit from outside</button>
      <PlayerForm bindSubmitForm={bindSubmitFormB} />
      <PlayerForm bindSubmitForm={bindSubmitForm} />
      <ButtonBar></ButtonBar>
    </div>
  );
};

export default CombatantForm;
