import React from "react";
import { useFormik, Formik, Form, FieldArray } from "formik";
import * as Yup from "yup";
import { generate } from "shortid";
import { TextField, Button } from "@mui/material";

const FormB = ({ onChange, refId }, props) => {
  const formik = useFormik({
    initialValues: {
      phone: "",
    },
    validationSchema: Yup.object({
      phone: Yup.string()
        .required("This field is required")
        .min(6, "At least 06 characters"),
    }),
    onSubmit: () => {
      watchForm();
    },
  });

  React.useImperativeHandle(refId, () => ({
    Submit: async () => {
      await formik.submitForm();
    },
  }));

  function watchForm() {
    if (onChange) {
      onChange({
        values: formik.values,
        validated: formik.isSubmitting
          ? Object.keys(formik.errors).length === 0
          : false,
      });
    }
  }

  return (
    <form ref={refId}>
      <Form>
        <FieldArray>
          {({ values, handleChange, errors }) => (
            <Form>
              <FieldArray name="combatants">
                {({ push, remove }) => (
                  <div>
                    <div>
                      <h2>{props.name}</h2>
                      <Button
                        type="button"
                        onClick={() =>
                          push({
                            id: generate(),
                            name: "",
                            hp: "",
                          })
                        }
                      >
                        Add New Field
                      </Button>
                    </div>
                    {values.combatants.map((combatant, index) => {
                      const name = `combatants[${index}.name]`;
                      return (
                        <div key={combatant.id}>
                          <TextField
                            name={name}
                            value={combatant.name}
                            onChange={handleChange}
                          />
                          <TextField
                            name={`combatants[${index}.hp]`}
                            value={combatant.hp}
                            onChange={handleChange}
                          />
                          <Button onClick={() => remove(index)}>x</Button>
                        </div>
                      );
                    })}
                  </div>
                )}
              </FieldArray>
              <button type="submit">Submit</button>
              <pre>{JSON.stringify(values, null, 2)}</pre>
            </Form>
          )}
        </FieldArray>
      </Form>
      <div className={"form"}>
        <h3>Form B</h3>
        <div>
          <label>Phone number </label>
          <input
            name="phone"
            value={formik.values.phone}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
          />
          {formik.errors.phone && (
            <div style={{ color: "red" }}>
              <small>{formik.errors.phone}</small>
            </div>
          )}
        </div>

        <pre>{JSON.stringify(formik.values, null, 2)}</pre>
      </div>
    </form>
  );
};

export default FormB;
