import "./App.css";
import React from "react";
import CombatantFormFields from "./components/CombatantFormFields";
import CombatantForm from "./components/CombatantForm";
import { DataProvider } from "./DataContext";
import BattleTracker from "./components/BattleTracker";
import ButtonBar from "./components/ButtonBar";

function App() {
  return (
    <div className="app">
      <DataProvider>
        <CombatantForm>
          <CombatantFormFields name="Players" />
          <CombatantFormFields name="Enemies" />
        </CombatantForm>
        <BattleTracker />
        <ButtonBar />
      </DataProvider>
    </div>
  );
}

export default App;
