import React, { useState } from "react";
import { useFormik, Form, Field, FieldArray } from "formik";
import * as Yup from "yup";

const FormA = ({ onChange, refId }) => {
  const formik = useFormik({
    initialValues: {
      friends: [{ age: "", name: "" }],
    },
    validationSchema: Yup.object({
      name: Yup.string().required("This field is required"),
      hp: Yup.string().required("This field is required"),
      init: Yup.string().required("This field is required"),
    }),
    onSubmit: () => {
      watchForm();
    },
  });

  React.useImperativeHandle(refId, () => ({
    Submit: async () => {
      await formik.submitForm();
    },
  }));

  function watchForm() {
    if (onChange) {
      onChange({
        values: formik.values,
        validated: formik.isSubmitting
          ? Object.keys(formik.errors).length === 0
          : false,
      });
    }
  }

  return (
    <form ref={refId}>
      <div>
        {initialValues.friends.map((combatant, index) => (
          <div key={index}>
            <input
              type="text"
              name="name"
              placeholder="name"
              value={combatant.name}
            ></input>
            <input
              type="text"
              name="hp"
              placeholder="hp"
              value={combatant.hp}
            ></input>
          </div>
        ))}
      </div>
      <div className={"form"}>
        <h3>Form A</h3>
        <div>
          <label>Name </label>
          <input
            name="name"
            value={formik.values.name}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
          />
          {formik.errors.name && (
            <div style={{ color: "red" }}>
              <small>{formik.errors.name}</small>
            </div>
          )}
        </div>
        <br />
        <div>
          <label>HP </label>
          <input
            name="hp"
            value={formik.values.hp}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
          />
          {formik.errors.email && (
            <div style={{ color: "red" }}>
              <small>{formik.errors.email}</small>
            </div>
          )}
        </div>
        <div>
          <label>INIT </label>
          <input
            name="init"
            value={formik.values.init}
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
          />
          {formik.errors.email && (
            <div style={{ color: "red" }}>
              <small>{formik.errors.email}</small>
            </div>
          )}
        </div>
        <pre>{JSON.stringify(formik.values, null, 2)}</pre>
      </div>
    </form>
  );
};

export default FormA;
